mapper.select:


    and delete_flag=0

        and dept_id = #{deptId}


        and project_id = #{projectId}


        and temporary_code = #{temporaryCode}


        and temporary_code like concat('%', #{searchTemporaryCode}, '%')


        and temporary_code like concat(#{leftSearchTemporaryCode}, '%')


        and original_code = #{originalCode}


        and original_code in

            #{code}



        and original_code like concat('%', #{searchOriginalCode}, '%')


        and original_sub_code = #{originalSubCode}


        and original_sub_code like concat(#{leftLikeOriginalSubCode}, '%')


        and category_id = #{categoryId}


        and category_name like concat('%', #{categoryName}, '%')


        and brand_id = #{brandId}


        and brand_name like concat('%', #{brandName}, '%')


        and material_id = #{materialId}


        and asset_name = #{assetName}


        and asset_name like concat('%', #{searchAssetName}, '%')


        and specification = #{specification}


        and specification like concat('%', #{searchSpecification}, '%')


        and a.asset_status = #{assetStatus}


        and production_time = #{productionTime}


        and location_id = #{locationId}


        and location_name =#{locationName}


        and location_name like concat('%', #{searchLocationName}, '%')


        and managed_dept_Id = #{managedDeptId}


        and managed_dept_name =#{managedDeptName}


        and managed_dept_name like concat('%', #{searchManagedDeptName}, '%')


        and using_dept_Id = #{usingDeptId}


        and using_dept_name =#{usingDeptName}


        and using_dept_name like concat('%', #{searchUsingDeptName}, '%')


        and managed_emp_Id = #{managedEmpId}


        and managed_emp_name =#{managedEmpName}


        and managed_emp_name like concat('%', #{searchManagedEmpName}, '%')


        and using_emp_Id = #{usingEmpId}


        and using_emp_name =#{usingEmpName}


        and using_emp_name like concat('%', #{searchUsingEmpName}, '%')


        and collector_user_id = #{collectorUserId}


        and collector_user_name =#{collectorUserName}


        and collector_user_name like concat('%', #{searchCollectorUserName}, '%')


        and collector_time = #{collectorTime}


        and main_image_url = #{mainImageUrl}


        and main_image_name like concat('%', #{mainImageName}, '%')


        and image_url = #{imageUrl}


        and image_url_name like concat('%', #{imageUrlName}, '%')


        and delete_flag = #{deleteFlag}


        and created_by_id = #{createdById}


        and created_by_name like concat('%', #{createdByName}, '%')


        and created_time = #{createdTime}


        and updated_by_id = #{updatedById}


        and updated_by_name like concat('%', #{updatedByName}, '%')


        and updated_time = #{updatedTime}


        and (
        managed_dept_Id = #{searchDeptId}
        or using_dept_Id = #{searchDeptId}
        )


        and (
        temporary_code like concat('%', #{searchName}, '%')
        or original_code like concat('%', #{searchName}, '%')
        or asset_name like concat('%', #{searchName}, '%')
        )


    ${params.dataScope}

order by a.collector_time desc ,a.id desc

select a.id,
a.dept_id,
a.project_id,
a.temporary_code,
a.original_code,
a.original_sub_code,
a.category_id,
a.category_name,
a.brand_id,
a.brand_name,
a.material_id,
a.asset_name,
a.specification,
a.asset_status,
a.production_time,
a.location_id,
ifnull((select location_name from location as ls where a.location_id = ls.id), a.location_name) as
location_name,
a.managed_dept_Id,
a.managed_dept_name,
a.using_dept_Id,
a.using_dept_name,
a.managed_emp_Id,
a.managed_emp_name,
a.using_emp_Id,
a.using_emp_name,
a.collector_user_id,
a.collector_user_name,
a.collector_time,
a.remark,
a.main_image_url,
a.main_image_name,
a.image_url,
a.image_url_name,
a.match_status,
a.print_status,
a.delete_flag,
a.created_by_id,
a.created_by_name,
a.created_time,
a.updated_by_id,
a.updated_by_name,
a.updated_time,
sd.dept_name,
p.project_name
from asset as a
inner join sys_dept sd on a.dept_id = sd.dept_id and sd.del_flag = '0'
inner join project p on a.project_id = p.id and p.delete_flag = 0

    and a.delete_flag=0

        and a.dept_id = #{deptId}


        and a.project_id = #{projectId}


        and a.temporary_code = #{temporaryCode}


        and a.temporary_code like concat('%', #{searchTemporaryCode}, '%')


        and a.original_code = #{originalCode}


        and a.original_code like concat('%', #{searchOriginalCode}, '%')


        and a.category_id = #{categoryId}


        and a.category_name like concat('%', #{categoryName}, '%')


        and a.brand_id = #{brandId}


        and a.brand_name like concat('%', #{brandName}, '%')


        and a.material_id = #{materialId}


        and a.asset_name = #{assetName}


        and a.asset_name like concat('%', #{searchAssetName}, '%')


        and a.specification = #{specification}


        and a.specification like concat('%', #{searchSpecification}, '%')


        and a.asset_status = #{assetStatus}


        and a.production_time = #{productionTime}


        and a.location_id = #{locationId}


        and a.location_name =#{locationName}


        and a.location_name like concat('%', #{searchLocationName}, '%')


        and a.managed_dept_Id = #{managedDeptId}


        and a.managed_dept_name =#{managedDeptName}


        and a.managed_dept_name like concat('%', #{searchManagedDeptName}, '%')


        and a.using_dept_Id = #{usingDeptId}


        and a.using_dept_name =#{usingDeptName}


        and a.using_dept_name like concat('%', #{searchUsingDeptName}, '%')


        and a.managed_emp_Id = #{managedEmpId}


        and a.managed_emp_name =#{managedEmpName}


        and a.managed_emp_name like concat('%', #{searchManagedEmpName}, '%')


        and a.using_emp_Id = #{usingEmpId}


        and a.using_emp_name =#{usingEmpName}


        and a.using_emp_name like concat('%', #{searchUsingEmpName}, '%')


        and a.collector_user_id = #{collectorUserId}


        and a.collector_user_name =#{collectorUserName}


        and a.collector_user_name like concat('%', #{searchCollectorUserName}, '%')


        and a.collector_time = #{collectorTime}


        and a.main_image_url = #{mainImageUrl}


        and a.main_image_name like concat('%', #{mainImageName}, '%')


        and a.image_url = #{imageUrl}


        and a.image_url_name like concat('%', #{imageUrlName}, '%')


        and a.print_status = #{printStatus}


        and a.match_status = #{matchStatus}


        and a.delete_flag = #{deleteFlag}


        and a.created_by_id = #{createdById}


        and a.created_by_name like concat('%', #{createdByName}, '%')


        and a.created_time = #{createdTime}


        and a.updated_by_id = #{updatedById}


        and a.updated_by_name like concat('%', #{updatedByName}, '%')


        and a.updated_time = #{updatedTime}


        and (
        a.managed_dept_Id = #{searchDeptId}
        or a.using_dept_Id = #{searchDeptId}
        )


        and (
        a.temporary_code like concat('%', #{searchName}, '%')
        or a.original_code like concat('%', #{searchName}, '%')
        or a.asset_name like concat('%', #{searchName}, '%')
        )


        and (
        a.temporary_code = #{matchCode}
        or a.original_sub_code = #{matchCode}
        )



        AND date_format(a.collector_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')



        AND date_format(a.collector_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')


    ${params.dataScope}

order by a.collector_time desc ,a.id desc


where id in

    #{id}

and delete_flag=0

select count(id)                       as totalCount,
       sum(if(match_status = 1, 1, 0)) as checkCount
from asset as a
where delete_flag = 0
  and dept_id = #{deptId}
  and project_id = #{projectId}

select count(id)                       as totalCount,
       sum(if(match_status = 1, 1, 0)) as checkCount
from asset as a
where delete_flag = 0
  and project_id = #{projectId}
  and asset_name = #{assetName}

select count(id)                       as totalCount,
       sum(if(match_status = 1, 1, 0)) as checkCount
from asset as a
where delete_flag = 0
  and project_id = #{projectId}
  and using_dept_Id = #{usingDeptId}

select count(id)                       as totalCount,
       sum(if(match_status = 1, 1, 0)) as checkCount
from asset as a
where delete_flag = 0
  and project_id = #{projectId}
  and location_id = #{locationId}

select metricsId, metricsName, totalCount, checkCount from ( select a.category_id as metricsId, c.category_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join category c on a.category_id = c.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and c.category_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.category_id ) as data order by data.totalCount desc

select metricsId, metricsName, totalCount, checkCount from ( select a.brand_name as metricsId, b.brand_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join brand as b on a.brand_id = b.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and b.brand_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.brand_name ) as data order by data.totalCount desc

select metricsId, metricsName, totalCount, checkCount from ( select asset_name as metricsId, asset_name as metricsName, count(id) as totalCount, sum(if(match_status = 1, 1, 0)) as checkCount from asset as a where delete_flag = 0 and a.match_status = 0 and dept_id = #{reqBO.deptId} and project_id = #{reqBO.projectId}
and asset_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by asset_name ) as data order by data.totalCount desc

select metricsId, metricsName, totalCount, checkCount from ( select specification as metricsId, specification as metricsName, count(id) as totalCount, sum(if(match_status = 1, 1, 0)) as checkCount from asset as a where delete_flag = 0 and a.match_status = 0 and dept_id = #{reqBO.deptId} and project_id = #{reqBO.projectId}
and specification like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by specification ) as data order by data.totalCount desc

select metricsId, metricsName, totalCount, checkCount from ( select a.location_id as metricsId, l.location_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join location as l on a.location_id = l.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and l.location_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.location_id ) as data order by data.checkCount desc

select metricsId, metricsName, totalCount, checkCount from ( select a.managed_dept_Id as metricsId, d.department_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join department as d on a.managed_dept_Id=d.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and d.department_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.managed_dept_Id ) as data order by data.checkCount desc

select metricsId, metricsName, totalCount, checkCount from ( select a.using_dept_Id as metricsId, d.department_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join department as d on a.using_dept_Id=d.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and d.department_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.using_dept_Id ) as data order by data.checkCount desc

select metricsId, metricsName, totalCount, checkCount from ( select a.managed_emp_Id as metricsId, e.employee_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join employee as e on a.managed_emp_Id = e.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and e.employee_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.managed_emp_Id ) as data order by data.totalCount desc

select metricsId, metricsName, totalCount, checkCount from ( select a.using_emp_Id as metricsId, e.employee_name as metricsName, count(a.id) as totalCount, sum(if(a.match_status = 1, 1, 0)) as checkCount from asset as a inner join employee as e on a.using_emp_Id = e.id where a.delete_flag = 0 and a.match_status = 0 and a.dept_id = #{reqBO.deptId} and a.project_id = #{reqBO.projectId}
and e.employee_name like concat('%', #{reqBO.searchName}, '%')
${reqBO.params.dataScope} group by a.using_emp_Id ) as data order by data.totalCount desc

SELECT COUNT(1)
FROM asset
WHERE delete_flag = 0
  and original_code = #{originalCode}
  and project_id = #{projectId}
