select.where:
and delete_flag=0

    and dept_id = #{deptId}


    and project_id = #{projectId}


    and biz_id = #{bizId}


    and biz_key = #{bizKey}


    and metrics_name like concat('%', #{metricsName}, '%')


    and metrics_type = #{metricsType}


    and biz_type = #{bizType}


    and total_count = #{totalCount}


    and check_count = #{checkCount}


    and delete_flag = #{deleteFlag}


    and created_by_id = #{createdById}


    and created_by_name like concat('%', #{createdByName}, '%')


    and created_time = #{createdTime}


    and updated_by_id = #{updatedById}


    and updated_by_name like concat('%', #{updatedByName}, '%')


    and updated_time = #{updatedTime}

mapper.select:
select a.id, a.dept_id, a.project_id, a.temporary_code, a.original_code, a.original_sub_code, a.category_id, a.category_name, a.brand_id, a.brand_name, a.material_id, a.asset_name, a.specification, a.asset_status, a.production_time, a.location_id, ifnull(ls.location_name,a.location_name) as location_name, a.managed_dept_Id, a.managed_dept_name, a.using_dept_Id, a.using_dept_name, a.managed_emp_Id, a.managed_emp_name, a.using_emp_Id, a.using_emp_name, a.collector_user_id, a.collector_user_name, a.collector_time, a.remark, a.main_image_url, a.main_image_name, a.image_url, a.image_url_name, a.match_status, a.print_status, a.delete_flag, a.created_by_id, a.created_by_name, a.created_time, a.updated_by_id, a.updated_by_name, a.updated_time from asset as a left join location ls on a.location_id = ls.id inner join ( select distinct temp.id, ifnull(ma.total_count,0) as name_total_count, ifnull(md.total_count,0) as dept_total_count, ifnull(ml.total_count,0) as location_total_count from asset as temp left join metrics ma on ma.delete_flag = 0 and ma.biz_type = 1 and temp.dept_id = ma.dept_id and temp.project_id = ma.project_id and ma.metrics_type = 1 and temp.asset_name = ma.biz_key left join metrics md on md.delete_flag = 0 and md.biz_type = 1 and temp.dept_id = md.dept_id and temp.project_id = md.project_id and md.metrics_type = 2 and temp.using_dept_Id = md.biz_id left join metrics ml on ml.delete_flag = 0 and ml.biz_type = 1 and temp.dept_id = ml.dept_id and temp.project_id = ml.project_id and ml.metrics_type = 3 and temp.location_id = ml.biz_id where temp.delete_flag = 0 and temp.dept_id = #{deptId} and temp.project_id = #{projectId} and temp.match_status = 0 ) as da on a.id=da.id where a.delete_flag = 0
AND a.category_id in
#{categoryId}
AND a.brand_name in
#{brandName}
AND a.asset_name in
#{assetName}
AND a.specification in
#{specification}
AND a.location_id in
#{locationId}
AND a.managed_dept_Id in
#{managedDeptId}
AND a.using_dept_Id in
#{usingDeptId}
AND a.managed_emp_Id in
#{managedEmpId}
AND a.using_emp_Id in
#{usingEmpId}
order by
a.category_name
da.name_total_count desc
da.dept_total_count desc
da.location_total_count desc
a.using_emp_Id
a.brand_name
a.specification
a.id

select a.id, a.dept_id, a.project_id, a.original_code, a.category_id, a.category_name, a.brand_name, a.asset_name, a.specification, a.obtain_time, a.product_price, a.location_id, ifnull(ls.location_name,a.location_name) as location_name, a.managed_dept_Id, a.managed_dept_name, a.using_dept_Id, a.using_dept_name, a.managed_emp_Id, a.managed_emp_name, a.using_emp_Id, a.using_emp_name, a.remark, a.match_status, a.match_count, a.delete_flag, a.created_by_id, a.created_by_name, a.created_time, a.updated_by_id, a.updated_by_name, a.updated_time from original_asset as a left join location ls on a.location_id = ls.id inner join( select distinct temp.id, ifnull(ma.total_count,0) as name_total_count, ifnull(md.total_count,0) as dept_total_count, ifnull(ml.total_count,0) as location_total_count from original_asset temp left join metrics ma on ma.delete_flag = 0 and ma.biz_type = 2 and temp.dept_id = ma.dept_id and temp.project_id = ma.project_id and ma.metrics_type = 1 and temp.asset_name = ma.biz_key left join metrics md on md.delete_flag = 0 and md.biz_type = 2 and temp.dept_id = md.dept_id and temp.project_id = md.project_id and md.metrics_type = 2 and temp.using_dept_Id = md.biz_id left join metrics ml on ml.delete_flag = 0 and ml.biz_type = 2 and temp.dept_id = ml.dept_id and temp.project_id = ml.project_id and ml.metrics_type = 3 and temp.location_id = ml.biz_id where temp.delete_flag = 0 and temp.dept_id = #{deptId} and temp.project_id = #{projectId} and temp.match_status = 0 ) as da on a.id=da.id where a.delete_flag = 0
AND a.category_id in
#{categoryId}
AND a.brand_name in
#{brandName}
AND a.asset_name in
#{assetName}
AND a.specification in
#{specification}
AND a.location_id in
#{locationId}
AND a.managed_dept_Id in
#{managedDeptId}
AND a.using_dept_Id in
#{usingDeptId}
AND a.managed_emp_Id in
#{managedEmpId}
AND a.using_emp_Id in
#{usingEmpId}
order by
a.category_name
da.name_total_count desc
da.dept_total_count desc
da.location_total_count desc
a.using_emp_Id
a.brand_name
a.specification
a.obtain_time_day desc
a.obtain_time_day desc

mapper.update:
delete from metrics where project_id = #{projectId} and biz_type = 1;
insert into metrics(dept_id, project_id, biz_id, biz_key, metrics_name, metrics_type, biz_type, total_count, check_count, delete_flag, created_by_id, created_by_name, created_time, updated_by_id, updated_by_name, updated_time) select a.dept_id, a.project_id, 0, a.asset_name, a.asset_name, 1 as metrics_type, 1 as biz_type, count(a.id) as totalCount, sum(if(match_status >= 1, 1, 0)) as checkCount, 0, 0, 'system', now(), 0, 'system', now() from asset as a where a.delete_flag = 0 and a.project_id = #{projectId} group by a.dept_id, a.project_id, a.asset_name;
insert into metrics(dept_id, project_id, biz_id, biz_key, metrics_name, metrics_type, biz_type, total_count, check_count, delete_flag, created_by_id, created_by_name, created_time, updated_by_id, updated_by_name, updated_time) select a.dept_id, a.project_id, a.using_dept_Id, a.using_dept_Id, a.using_dept_name, 2 as metrics_type, 1 as biz_type, count(a.id) as totalCount, sum(if(match_status >= 1, 1, 0)) as checkCount, 0, 0, 'system', now(), 0, 'system', now() from asset as a where a.delete_flag = 0 and a.project_id = #{projectId} group by a.dept_id, a.project_id, a.using_dept_Id;
insert into metrics(dept_id, project_id, biz_id, biz_key, metrics_name, metrics_type, biz_type, total_count, check_count, delete_flag, created_by_id, created_by_name, created_time, updated_by_id, updated_by_name, updated_time) select a.dept_id, a.project_id, a.location_id, a.location_id, ifnull(ls.location_name,a.location_name), 3 as metrics_type, 1 as biz_type, count(a.id) as totalCount, sum(if(match_status >= 1, 1, 0)) as checkCount, 0, 0, 'system', now(), 0, 'system', now() from asset as a left join location ls on a.location_id = ls.id where a.delete_flag = 0 and a.project_id = #{projectId} group by a.dept_id, a.project_id, a.location_id;

delete from metrics where project_id = #{projectId} and biz_type = 2;
insert into metrics(dept_id, project_id, biz_id, biz_key, metrics_name, metrics_type, biz_type, total_count, check_count, delete_flag, created_by_id, created_by_name, created_time, updated_by_id, updated_by_name, updated_time) select a.dept_id, a.project_id, 0, a.asset_name, a.asset_name, 1 as metrics_type, 2 as biz_type, count(a.id) as totalCount, sum(if(match_status >= 1, 1, 0)) as checkCount, 0, 0, 'system', now(), 0, 'system', now() from original_asset as a where a.delete_flag = 0 and a.project_id = #{projectId} group by a.dept_id, a.project_id, a.asset_name;
insert into metrics(dept_id, project_id, biz_id, biz_key, metrics_name, metrics_type, biz_type, total_count, check_count, delete_flag, created_by_id, created_by_name, created_time, updated_by_id, updated_by_name, updated_time) select a.dept_id, a.project_id, a.using_dept_Id, a.using_dept_Id, a.using_dept_name, 2 as metrics_type, 2 as biz_type, count(a.id) as totalCount, sum(if(match_status >= 1, 1, 0)) as checkCount, 0, 0, 'system', now(), 0, 'system', now() from original_asset as a where a.delete_flag = 0 and a.project_id = #{projectId} group by a.dept_id, a.project_id, a.using_dept_Id;
insert into metrics(dept_id, project_id, biz_id, biz_key, metrics_name, metrics_type, biz_type, total_count, check_count, delete_flag, created_by_id, created_by_name, created_time, updated_by_id, updated_by_name, updated_time) select a.dept_id, a.project_id, a.location_id, a.location_id, ifnull(ls.location_name,a.location_name), 3 as metrics_type, 2 as biz_type, count(a.id) as totalCount, sum(if(match_status >= 1, 1, 0)) as checkCount, 0, 0, 'system', now(), 0, 'system', now() from original_asset as a left join location ls on a.location_id = ls.id where a.delete_flag = 0 and a.project_id = #{projectId} group by a.dept_id, a.project_id, a.location_id;
